import getApi from "../api/WeatherSource";

function Searchbar(props) {
    // const [city,setCity]= useState(null);

    // const [Search,setSearch]= useState("London");

    useEffect(() => {
        const fetchApi=async()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${props.search}&appid=e899757c1162bf7b2b2684a0b2b8f5cc`
            const response=await fetch(url);

            const resJson=await response.json();
            // console.log(resJson);
            props.setCity(resJson);
        }
        fetchApi();

    },[props.search])
    console.log(props);
    return (
        <div>
            <div className="control">
                <input className="input" onChange={props.city1} type="search" placeholder="Search" ></input>

            </div>
        </div>
    );
}

export default Searchbar;
