import React, { useState } from 'react'
import getApi from "../api/WeatherSource"

function Searchbar(props) {
    const {onSearch}=props;
    const {searchText,setsearchText}=useState('');
    const {handleInput}=(e)=>{
        const text=e.target.value;
        getApi()
        setsearchText(text)
    }
    const handleEnterKeyPressed=(e)=>{
        if(e.key==='Enter'){
            onSearch(searchText);
        }
    }
    return (
        <div>
            <div className="control">
                <input className="input" onChange={handleInput}  onKeyPress={handleEnterKeyPressed} value={searchText} type="text" placeholder="Search" ></input>
            </div>
        </div>
    );
}

export default Searchbar;
